import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/news/articles/')
      .then(response => response.json())
      .then(data => setArticles(data))
      .catch(error => console.error('Error:', error));
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1 style={{color: 'white', fontSize: 24}}>
          Top Articles:
        </h1>
      </header>
      <div className="articles" id="articles">
        {articles.map((article, index) => (
          <div key={index} style={{marginBottom: 15, textAlign: 'left'}}>
            <a href={article.url} target="_blank" rel="noopener noreferrer" style={{color: '#61dafb', fontSize: 18}}>
              {article.title}
            </a>
            <p style={{margin: 0, fontSize: 14}}>{article.description}</p>
            <img src={article.image_url} alt={article.title} style={{maxWidth: '20%', height: 'auto', marginTop: 5}} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
